{
	if(gb2312>127) 
	{
		uint16_t index=0;
		const uint8_t* _font=NULL;
		gb2312 = gb2312<<8 | c;
		for(index=0; index<sizeof(gb2312_font)/sizeof(gb2312_font[0]); index++)
		{
			if(gb2312==gb2312_font[index].gb)
			{
//				drawCharCN_16x16(cursor_x, cursor_y, WHITE, BLACK, 1, gb2312_font[index]._font);
//				cursor_x += textsize*16;
//				if (wrap && (cursor_x > (_width - textsize*16))) {
//				  cursor_y += textsize*16;
//				  cursor_x = 0;
//				}
				_font = gb2312_font[index]._font;
			}
		}
		gb2312 = 0;
		return _font;
	}
	gb2312 = c;
	return NULL;
}
