const uint8_t* gb2312_search(const uint8_t c)
{
	static uint16_t gb2312=0;
	uint16_t gb2312_r;
	if(gb2312>127) 
	{
		uint16_t index=0;
		const uint8_t* _font=NULL;
		gb2312 = gb2312<<8 | c;
		for(index=0; index<sizeof(gb2312_font)/sizeof(gb2312_font[0]); index++)
		{
			gb2312_r=(gb2312_font[index].gb[0]<<8)|gb2312_font[index].gb[1];
			if(gb2312==gb2312_r)
			{
				_font = gb2312_font[index]._font;
			}
		}
		gb2312 = 0;
		return _font;
	}
	gb2312 = c;
	return NULL;
}


